import React, { useState } from 'react';
import '../styles/studentSignUp.css';
import NavBar from '../components/navBar.jsx';
import Footer from '../components/footer';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function StudentSignUp() {
    const navigate = useNavigate();

    // Remove 'this.' from function calls and use 'state' directly
    const [state, setState] = useState({
        fName: '',
        lName: '',
        email: '',
        password: '',
    });

    const handleInput = (e) => {
        // Use 'state' directly instead of 'this.state'
        setState({
            ...state,
            [e.target.name]: e.target.value,
        });
    };

    const saveStudent = async (e) => {
        e.preventDefault();

        try {
            const res = await axios.post('http://127.0.0.1:8000/api/add-student', state);
            if (res.data.status === 200) {
                console.log(res.data.message);
                // Use 'setState' to update state instead of 'this.setState'
                setState({
                    fName: '',
                    lName: '',
                    email: '',
                    password: '',
                });
            }
        } catch (error) {
            console.error('Error saving student:', error);
        }
    };

    return (
        <div>
            <div className="nav">
                <NavBar />
                <hr />
            </div>

            <div className="hero"></div>

            <div className="signUpForm">
                <h1>Student SignUp</h1>

                <form action="" onSubmit={saveStudent}>
                    {inputs.map(({ label, id, type, name }, index) => (
                        <div className="inputs" key={index}>
                            {/* Use 'value' from 'state' instead of hardcoded values */}
                            <input onChange={handleInput} type={type} id={id} name={name} value={state[name]} placeholder={label} />
                        </div>
                    ))}

                    {/* Use 'type="submit"' for the submit button */}
                    <button type="submit">Sign Up</button>

                    <p>
                        I have already account. <a href="#">Login</a>{' '}
                    </p>
                </form>
            </div>
            <Footer />
        </div>
    );
}

export default StudentSignUp;
