import InstituteSideBar from '../../components/instituteSideBar'


function InsEvent() {

    const addEvent = [
        {
            name : 'Event name',
            id : 'eventName',
            type : 'text'

        },

        {
            name : 'Event Description',
            id : 'eventDescription',
            type : 'text'
        },

        {
            name : 'Add Image',
            id : 'addImage',
            type : 'file'
        }


    ]
  return (
    <div>
        <div className="institute">
        <div className="instituteSideBar">
        <InstituteSideBar/>
      </div>

      <div className="ventContent">
      <h2>Add your Course</h2>    
          {
            addCourse.map((item,index) => {
              return(              
                  <form action="" onSubmit={addInsCourse} >
                    <div className="insCourseInputs" key={index}>
                       <input onChange={handleInput} type={item.type} placeholder={item.name} value={state[item.name]} id={item.id} />
                    </div> 
                  </form>     
                               
              )
            })
          }

          <button>Add</button>

  
        </div>
    </div>
    </div>
  )
}

export default InsEvent
