  };

  return (
    <>

    <div className="images">
        {currentItem.map(image => {
            return(
                <div>
                    <img src={image.url} alt={image.title} />
                </div>

            )
        })}
    </div>
      <ReactPaginate
        breakLabel="..."
        nextLabel="next >"
        onPageChange={handlePageClick}
        pageRangeDisplayed={5}
        pageCount={pageCount}
        previousLabel="< previous"
        renderOnZeroPageCount={null}
      />
    </>
  );
}
