import React, {useState} from 'react';
import '../styles/login.css';
import NavBar from '../components/navBar.jsx';
import Footer from '../components/footer'
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const loggedUserEmail = localStorage.getItem('user_email')
const adminEmail = import.meta.env.VITE_ADMIN_EMAIL

function StudentSignUp() {
    const navigate = useNavigate()
    const SignIn = [
        
        {
            type: 'email',
            name: 'email',
            label: 'E Mail',
        },
        {
            type: 'password',
            name: 'password',
            label: 'Password',
        },
      
    ];

    const [state, setState] = useState({
        email: '',
        password: ''
    })

    const handleCallBack = (data, status) => {
        if (status === 200 ){
            localStorage.setItem('userToken', data.data.token)
            if(adminEmail === loggedUserEmail){
                navigate('/dashboard', {replace: true})
            }
            else{
                navigate('/login')
            }
            window.location.reload()
        }
    }


    

    return (
        <div>
            <div className="nav">
                <NavBar />
                <hr />
            </div>

            <div className="hero"></div>

            <div className="signInForm">
                <h1>Sign In</h1>

                <form action="" onSubmit={saveLogin}>
                     {SignIn.map(({ label, type, name }, index) => (
                        <div className="inputs" key={index}>
                            
                            <input onChange={handleInput} type={type} value={state[name]} name={name} placeholder={label} />
                        </div>
                    ))}

                    <button className='buttons' onClick={() => navigate('/mainHome')}>Sign Up</button>

                </form>
                   
               
            </div>
            <Footer/>
        </div>
    );
}

export default StudentSignUp;
